#usercfg entries forces audio through hdmi0 right next to usb-c port
echo "disable_overscan=1" >> /boot/firmware/usercfg.txt &&
echo "# Enable audio (loads snd_bcm2835)" >> /boot/firmware/usercfg.txt &&
echo "dtparam=audio=on" >> /boot/firmware/usercfg.txt &&
echo "hdmi_drive=2" >> /boot/firmware/usercfg.txt &&
echo "hdmi_force_edid_audio=1" >> /boot/firmware/usercfg.txt &&

#syscfg entries 
echo "dtparam=i2c_arm=on" >> /boot/firmware/syscfg.txt && 
echo "dtparam=spi=on" >> /boot/firmware/syscfg.txt &&
sed -i s/parport_pc//g modules-load.d/cups-filters.conf &&
sed -i s/#WaylandEnable=false/WaylandEnable=false/g /etc/gdm3/custom.conf





